import styled from "styled-components";
import { useEffect, useState } from "react";
import{ useNavigate, useParams } from "react-router-dom";

const Style = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;

  li{
    margin-top: 30px;
  }

  span{
    margin-left: 20px;
  }

  button{
    margin: 8px;
    width: 80px;
    font-size: 150%;
  }

  .info{
    display: flex;
    justify-content: space-between;
  }

  .total{
    margin-top: 20px;
  }
`

function App() {

  const [info, setInfo] = useState(null)

  useEffect(() => {
    fetch(`http://localhost:3001/shoping_list`)
    .then((response) => response.json())
    .then((data) => {
      setInfo(data)
    })

  }, [])

  // console.log(info)

  const handleQuantityButton = (id) => {

    let result = id + 1


    let putData = {
      "id" : info.id,
      "name" : info.name,
      "quantity" : result,
      "price" : info.price
    }

    fetch(`http://localhost:3001/shoping_list`, {
      method: 'PUT',
      headers: {'Content-Type' : 'application/json'},
      body: JSON.stringify(putData)
    }).then(() => {
      console.log('성공')
    }).catch(() => {
      console.log('실패')
    })

  }

  const handleQuantityButton2 = (id) => {

  }

  return (
    <Style>
      <div>
        {info.map((el, idx) => {
          return (
            <>
              <li key={idx}>{el.name}</li>
              <div className="info">
                <span>{el.price}</span>
                <span>{el.quantity}</span>
              </div>
              <button onClick={() => {
                handleQuantityButton(el.quantity)
              }}>+</button>
              <button onClick={() => {
                handleQuantityButton2(el.id)
              }}>-</button>
            </>
          )
        })}
        <div className="total">total : </div>
      </div>
    </Style>
  );
}

export default App;
