import styled from "styled-components";
import { useEffect, useState } from "react";

const Style = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;

  li{
    margin-top: 30px;
  }

  span{
    margin-left: 20px;
  }

  button{
    margin: 8px;
    width: 80px;
    font-size: 150%;
  }

  .info{
    display: flex;
    justify-content: space-between;
  }

  .total{
    margin-top: 20px;
  }
`

function App() {

  const [info, setInfo] = useState(null)
  const [pri, setPri] = useState(0)

  useEffect(() => {
    fetch(`http://localhost:3001/shoping_list`)
    .then((response) => response.json())
    .then((data) => {
      setInfo(data)
    }, [])

  })

  const handleQuantityButton = (id) => {



    let putData = {
      'id' : info[id].id,
      'name' : info[id].name,
      'quantity' : info[id].quantity += 1,
      'price' : info[id].price
    }

    fetch(`http://localhost:3001/shoping_list/${id}`, {
      method: 'PUT',
      headers: {'Content-Type' : 'application/json'},
      body: JSON.stringify(putData)
    }).then(() => {
      console.log('더하기')
    }).catch(() => {
      console.log('err')
    })

  }

  const handleQuantityButton2 = (id) => {

    let reesult = info[id].quantity - 1

    if(info[id].quantity === 0){
      reesult = 0
    }

    let putData = {
      'id' : info[id].id,
      'name' : info[id].name,
      'quantity' : reesult,
      'price' : info[id].price
    }

    fetch(`http://localhost:3001/shoping_list/${id}`, {
      method: 'PUT',
      headers: {'Content-Type' : 'application/json'},
      body: JSON.stringify(putData)
    }).then(() => {
      console.log('빼기')
    }).catch(() => {
      console.log('err')
    })

  }

  const resultPrice = (e) => {

    let res = res + e

    setPri(res)

  }

  return (
    <Style>
      <div>
        {info.map((el, idx) => {
          return (
            <>
              <li key={idx}>{el.name}</li>
              <div className="info">
                <span>{el.price}</span>
                <span>{el.quantity}</span>
              </div>
              <button onClick={() => {
                handleQuantityButton(el.id)
              }}>+</button>
              <button onClick={() => {
                handleQuantityButton2(el.id);
                resultPrice(el.price * -1);
              }}>-</button>
            </>
          )
        })}
        <div className="total">total : {pri}</div>
      </div>
    </Style>
  );
}

export default App;
