import styled from "styled-components";
import { useEffect, useState } from "react";
import{ useNavigate, useParams } from "react-router-dom";

const Style = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;

  li{
    margin-top: 30px;
  }

  span{
    margin-left: 20px;
  }

  button{
    margin: 8px;
    width: 80px;
    font-size: 150%;
  }

  .info{
    display: flex;
    justify-content: space-between;
  }
`

function App() {

  const [info, setInfo] = useState(null)

  useEffect(() => {
    fetch(`http://localhost:3001/shoping_list`)
    .then((response) => response.json())
    .then((data) => {
      setInfo(data)
    })

  }, [])

  // console.log(info)

  return (
    <Style>
      <div>
        {info.map((el, idx) => {
          return (
            <>
              <li key={idx}>{el.name}</li>
              <div className="info">
                <span>{el.price}</span>
                <span>{el.quantity}</span>
              </div>
              <button>+</button>
              <button>-</button>
            </>
          )
        })}
        <div>total : </div>
      </div>
    </Style>
  );
}

export default App;
